data.command:
////////////////////////////////////////////////
// #READ FIRST
// # Remade version of Hunters Mark by Tposney
// # Requires Midi-qol On Use
// # Create Item as a Cantrip At-will for concentration ease
///////////////////////////////////////////////
if (args[0].hitTargets.length === 0) return;
if (args[0].tag === "OnUse") {
    let targetUuid = args[0].hitTargets[0].uuid;
    let actorD = MidiQOL.MQfromActorUuid(args[0].actorUuid);
    if (!actor || !targetUuid) {
        console.error("Favored Foe: no token/target selected");
        return;
    }
    let tokenD = canvas.tokens.get(args[0].tokenId);
    const effectData = {
        changes: [
            { key: "flags.midi-qol.favoredFoe", mode: 5, value: targetUuid, priority: 20 },
            { key: "flags.midi-qol.concentration-data.targets", mode: 2, value: { "actorId": actorD.id, "tokenId": tokenD.id }, priority: 20 },
            { key: "flags.dnd5e.DamageBonusMacro", mode: 0, value: `ItemMacro.${args[0].item.name}`, priority: 20 }
        ],
        origin: args[0].itemUuid,
        disabled: false,
        duration: duplicate(args[0].item.effects[0].duration),
        icon: args[0].item.img,
        label: args[0].item.name
    }
    effectData.duration.startTime = game.time.worldTime;
    actorD.createEmbeddedDocuments("ActiveEffect", [effectData]);
} else if (args[0].tag === "DamageBonus") {
    if (!["mwak", "rwak"].includes(args[0].item.data.actionType)) return {};
    let targetUuid = args[0].hitTargets[0].uuid;
    let actorD = canvas.tokens.get(args[0].tokenId).actor;
    let itemD = actorD.items.getName("Favored Foe");
    if (targetUuid !== getProperty(args[0].actor.flags, "midi-qol.favoredFoe")) return {};
    if (args[0].actor.effects.find(i => i.data.label === `${itemD.name} Effect`)) return {};
    let gameRound = game.combat ? game.combat.round : 0;
    let effectData = {
        label: `${itemD.name} Effect`,
        icon: itemD.img,
        tint: "#8f0000",
        origin: itemD.uuid,
        duration: { turns: 1, startRound: gameRound, startTime: game.time.worldTime }
    };
    actorD.createEmbeddedDocuments("ActiveEffect", [effectData]);
    let damageType = args[0].item.data.damage.parts[0][1];
    let level = actorD.items.find(i=> i.name === "Ranger")?.data.data.levels;
    if(!level) return ui.notifications.error("Ranger class missing");
    let baseDie = level >= 14 ? 8 : level >= 6 ? 6 : 4;
    let diceMult = args[0].isCritical ? 2 : 1;
    return { damageRoll: `${diceMult}d${baseDie}[${damageType}]`, flavor: `(Favored Foe (${CONFIG.DND5E.damageTypes[damageType]}))` };
}
