(async()=>{
console.log("Friendly Fire");
if(!game.combat) return ui.notifications.error(`There is no combat currently on this scene.`);
const combatT = await canvas.tokens.get(game.combat.current.tokenId).name;
const getAttack = await Object.values(MidiQOL.Workflow.workflows).filter(i=> i.d20AttackRoll === 1 && i.actor.name === combatT);
if(getAttack.length === 0) return {};
let lastAttack = getAttack[getAttack.length -1];
const originT = await canvas.tokens.get(lastAttack.tokenId);
const originA = await game.actors.get(lastAttack.actor._id);
const originTarget = lastAttack.targets.values();
let firstTarget = originTarget.next().value;
const item = lastAttack.item;
let damage_dice = item.data.data.damage.parts[0][0];
let damage_type = item.data.data.damage.parts[0][1];
let distance = item.data.data.range.long != null ? item.data.data.range.long : item.data.data.range.value;
let final_target;
let friendly_targets = await canvas.tokens.placeables.filter(target => (canvas.grid.measureDistance(firstTarget.center, target.center) < distance*2 && originT.id != target.id && originT.data.disposition === target.data.disposition && !canvas.walls.checkCollision(new Ray(firstTarget.center, target.center))));
console.log(friendly_targets);
if(friendly_targets.length === 0) return ui.notifications.warn(`No other friendly targets nearby.`);
let roll = new Roll(`1d${friendly_targets.length} -1`).evaluate({async:false});
final_target = friendly_targets[roll.total];
let damageRoll = new Roll(`${damage_dice}`, originA.getRollData()).evaluate({async:false});
game.dice3d?.showForRoll(damageRoll);
new MidiQOL.DamageOnlyWorkflow(originA, originT, damageRoll.total, damage_type, [final_target], damageRoll, {itemData: item.data, itemCardId: "new"});
})();

