(async () => {
    if (!game.combat) return ui.notifications.error(`There is no combat currently on this scene.`);
    const combatT = game.combat.current.tokenId;
    const getAttack = await Object.values(MidiQOL.Workflow.workflows).filter(i => i.d20AttackRoll === 1 && i.tokenId === combatT);
    let lastAttack = getAttack[getAttack.length - 1];
    let originT = await canvas.tokens.get(lastAttack.tokenId);
    let ability = "dex";
    let DC = 10;
    let save = await originT.actor.rollAbilityTest(ability, { chatMessage: false });
    let success = "";
    if (save.total >= DC) {
        success = "saves";
    } else {
        success = "fails";
        await game.cub.addCondition("Prone", originT, { warn: false });
    }
    let trip_list = `<div class="midi-qol-flex-container"><div class="midi-qol-target-npc midi-qol-target-name" id="${originT.id}">${originT.name} ${success} with ${save.total}</div><div><img src="${originT.data.img}" width="30" height="30" style="border:0px"></div></div>`;
    let trip_results = `<div class="midi-qol-nobox midi-qol-bigger-text">${CONFIG.DND5E.abilities[ability]} Saving Throw DC: ${DC}</div><div><div class="midi-qol-nobox">${trip_list}</div></div>`;
    let chatMessage = await game.messages.get(args[0].itemCardId);
    let content = await duplicate(chatMessage.data.content);
    let searchString = /<div class="midi-qol-saves-display">[\s\S]*<div class="end-midi-qol-saves-display">/g;
    let replaceString = `<div class="midi-qol-saves-display"><div class="end-midi-qol-saves-display">${trip_results}`;
    content = await content.replace(searchString, replaceString);
    await chatMessage.update({ content: content });
    await ui.chat.scrollBottom();
})();

