data.command:
if (args[0].targets.length === 0) return ui.notifications.error(`Please select a single target.`);
if (args[0].tag === "OnUse") {
    const target = canvas.tokens.get(args[0].targets[0]._id);
    const actorD = game.actors.get(args[0].actor._id);
    const tokenD = canvas.tokens.get(args[0].tokenId);
    const itemD = args[0].item;
    const game_round = game.combat ? game.combat.round : 1;
    if (target.actor.effects.find(i => i.data.label === "Hexblade Life Tap")) return ui.notifications.warn(`Target is already under the effects of ${itemD.name}.`);
    bonusDamage(target, itemD, actorD, game_round);
    lifeTap(target, itemD, tokenD, game_round);
}

async function bonusDamage(target, itemD, actorD, game_round) {
    const effectData = [{
        changes: [
            { key: "flags.midi-qol.hexbladeMark", mode: 0, value: target.id, priority: 20 },
            { key: "flags.dnd5e.DamageBonusMacro", mode: 0, value: `ItemMacro.${itemD.name}`, priority: 20 }
        ],
        origin: args[0].uuid,
        disabled: false,
        duration: { rounds: 10, startRound: game_round, startTime: game.time.worldTime },
        icon: "systems/dnd5e/icons/skills/affliction_04.jpg",
        label: `Hexblade Bonus Damage`
    }]
    await actorD.createEmbeddedDocuments("ActiveEffect", effectData);
}

async function lifeTap(target, itemD, tokenD, game_round) {
    let effectData = {
        label: `Hexblade Life Tap`,
        icon: "systems/dnd5e/icons/skills/affliction_01.jpg",
        origin: args[0].uuid,
        disabled: false,
        duration: { rounds: 10, startRound: game_round, startTime: game.time.worldTime },
        flags: { dae: { tokenId: tokenD.id, itemData: itemD } },
        changes: [
            { key: "macro.execute", mode: 0, value: "HexbladeLifeTap", priority: 20 }
        ]
    };
    await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: target.actor.uuid, effects: [effectData] });
}

if (args[0].tag === "DamageBonus") {
    console.log(args[0]);
    const target = canvas.tokens.get(args[0].targets[0]._id);
    const actorD = game.actors.get(args[0].actor._id);
    const damageType = args[0].damageDetail[0].type;
    if (target.id !== getProperty(actorD.data.flags, "midi-qol.hexbladeMark")) return {};
    if (args[0].attackRoll.dice[0].total === 19) {
        let damageNum = Number(args[0].damageRoll.dice[0].number);
        let damageDie = Number(args[0].damageRoll.dice[0].faces);
        let damageRoll = new Roll(`@prof + ${damageNum}d${damageDie}`, actorD.getRollData()).evaluate({ async: false });
        return { damageRoll: damageRoll.formula, flavor: "(Hexblade Critical)", damageList: args[0].damageList };
    } else if (args[0].attackRoll.dice[0].total === 20) {
        let damageNumber = Number(actorD.data.data.prof) * 2;
        return { damageRoll: damageNumber, flavor: "(Hexblade Critical)", damageList: args[0].damageList };
    } else {
        let damageNumber = actorD.data.data.prof;
        return { damageRoll: damageNumber, flavor: "(Hexblade Damage)", damageList: args[0].damageList };
    }
}
